import React, { useState } from "react";
import { UserCircle, LogOut, PlusCircle } from "lucide-react";
import Sidebar from "../components/Sidebar";
import CourseCard from "../components/CourseCard";
import FooterLanding from "../components/FooterLanding";
import CourseForm from "../components/CourseForm";
import ManageCourses from "../components/CourseList";
import { Link } from "react-router-dom";

export default function AdminDashboard() {
  const [view, setView] = useState("manageCourses");
  const [courses, setCourses] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const addCourse = (course) => setCourses([...courses, course]);
  const deleteCourse = (index) =>
    setCourses(courses.filter((_, i) => i !== index));

  return (
    <div className="dashboard-page flex flex-col h-screen bg-[#151922]">
      {/* Top Navigation Bar */}
      <div className="h-16 w-full flex items-center px-6 bg-white shadow-md relative">
        <img 
          src="/Edu_Sphere_Logo_yoyo.svg" 
          alt="EduSphere Logo" 
          className="h-10 cursor-pointer" 
          onClick={() => window.location.href = '/dashboard'} 
        />
        <div className="ml-auto relative">
          <button
            onClick={() => setDropdownOpen(!dropdownOpen)}
            className="focus:outline-none"
          >
            <UserCircle size={32} className="text-black hover:text-blue-600 transition bg-white" />
          </button>
          {dropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white shadow-md rounded-lg p-2 z-20 border border-gray-100">
              <button
                className="flex items-center bg-white text-black w-full p-2 hover:bg-blue-50 rounded-md transition-colors"
                onClick={() => setView("addCourse")}
              >
                <PlusCircle size={16} className="mr-2 text-blue-500" />
                Add Course
              </button>
              <button
                className="flex items-center bg-white text-black w-full p-2 hover:bg-blue-50 rounded-md transition-colors"
              >
                <LogOut size={16} className="mr-2 text-red-500" />
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Dashboard Layout */}
      <div className="dashboard-content flex flex-grow">
        <Sidebar />
        <div className="dashboard-main flex-grow p-8 bg-[#151922]">
          <h2 className="dashboard-title text-3xl font-semibold text-white mb-6">
            Admin Dashboard - Manage Courses
          </h2>

          {/* Display either Manage Courses or Add Course Form */}
          {view === "addCourse" && <CourseForm addCourse={addCourse} />}
          {view === "manageCourses" && (
            <div className="course-cards-container flex flex-col gap-6">
              {courses.length > 0 ? (
                courses.map((course, index) => (
                  <CourseCard
                    key={index}
                    title={course.title}
                    description={course.description}
                    status={course.difficulty_level.charAt(0).toUpperCase() + course.difficulty_level.slice(1).toLowerCase()} // Capitalized status
                    lessons={Array.from({ length: course.num_lessons }, (_, i) => ({
                      title: `Lesson ${i + 1}`,
                      link: `#`, // Replace with actual lesson link
                    }))}
                  />
                ))
              ) : (
                <div>No courses available.</div>
              )}
            </div>
          )}
        </div>
      </div>

      <FooterLanding />
    </div>
  );
}
