import React from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import CourseCard from '../components/CourseCard';
import FooterLanding from '../components/FooterLanding';


const HomePage = () => {
  return (
    <div className="home-page flex flex-col h-screen bg-gray-100">
      <Header />
      <div className="main-container flex flex-grow">
        <Sidebar />
        <main className="content-area flex-grow p-8 bg-white">
          <h2 className="page-title text-3xl font-bold mb-6 text-center text-gray-800">
            Free Courses Exclusive for You!
          </h2>
          <div className="course-cards-container grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            <CourseCard
              title="Mastering Digital Illustration"
              description="A complete guide from sketch to screen."
              status="Not Started"
              lessons={[
                { title: "Introduction to Digital Art", link: "/lesson/1" },
                { title: "Sketching Techniques", link: "/lesson/2" },
                { title: "Digital Painting Basics", link: "/lesson/3" }
              ]}
            />
            <CourseCard
              title="Flutter Development"
              description="A complete guide from sketch to screen."
              status="Started"
              lessons={[
                { title: "Introduction to Flutter", link: "/lesson/1" },
                { title: "Sketching Techniques", link: "/lesson/2" },
                { title: "Installation", link: "/lesson/3" }
              ]}
            />
            <CourseCard
              title="Service Now Developer"
              description="A complete guide from sketch to screen."
              status="Completed"
              lessons={[
                { title: "Introduction to Digital Art", link: "/lesson/1" },
                { title: "Sketching Techniques", link: "/lesson/2" },
                { title: "Digital Painting Basics", link: "/lesson/3" }
              ]}
            />
          </div>
        </main>
      </div>
      <FooterLanding />
    </div>
  );
};

export default HomePage;
