import React from "react";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import CourseCard from "../components/CourseCard";
import FooterLanding from "../components/FooterLanding";
import "./HomePage.css";

const HomePage = () => {
  return (
    <div className="home-page flex flex-col h-screen bg-gray-600">
      <Header />
      <div className="main-container flex flex-grow">
        <main className="freecontent flex-grow p-8 bg-gray-800">
          <h2 className="page-title text-3xl font-semibold text-white mb-6">
            Free Courses Exclusive for You!
          </h2>

          {/* Course Cards Section */}
          <div className="course-cards-container flex flex-col gap-6">
            {/* Course Card Example */}
            <CourseCard
              title="Mastering Digital Illustration"
              description="A complete guide from sketch to screen."
              status="Not Started"
              lessons={[
                { title: "Introduction to Digital Art", link: "/lesson/1" },
                { title: "Sketching Techniques", link: "/lesson/2" },
                { title: "Digital Painting Basics", link: "/lesson/3" }
              ]}
            />
            <CourseCard
              title="Flutter Development"
              description="A complete guide from sketch to screen."
              status="In Progress"
              lessons={[
                { title: "Introduction to Flutter", link: "/lesson/1" },
                { title: "Sketching Techniques", link: "/lesson/2" },
                { title: "Installation", link: "/lesson/3" }
              ]}
            />
            <CourseCard
              title="Service Now Developer"
              description="A complete guide from sketch to screen."
              status="Completed"
              lessons={[
                { title: "Introduction to Digital Art", link: "/lesson/1" },
                { title: "Sketching Techniques", link: "/lesson/2" },
                { title: "Digital Painting Basics", link: "/lesson/3" }
              ]}
            />
          </div>
        </main>
      </div>
      <FooterLanding />
    </div>
  );
};

export default HomePage;
